# In[57]:


def rmse(target, predict):
    
    target = np.array(target)
    predict = np.array(predict)
    diff = predict-target
    square_diff = diff ** 2
    mean_square_diff = square_diff.mean()
    rmse = np.sqrt(mean_square_diff)
    return rmse


